import React, { Component } from "react";

function ImageLink(props) {
    return (
      <div class="gallery">
        <a href={props.imgName}>
          <img src={props.imgName} width="600" height="400"/>
        </a>
      </div>
    );
}

function ImageTD(props) {
    return (
      <td class="gallery">
        <a href={props.imgName}>
          <img src={props.imgName} width="600" height="400"/>
        </a>
      </td>
    );
}

class Gallery extends Component {

  state = {
    photosList: [
      'images/1.png',
      'https://scontent.fewr1-6.fna.fbcdn.net/v/t1.0-0/p640x640/86296455_2573592766189146_3731961409219592192_o.jpg?_nc_cat=102&_nc_oc=AQlun9NCstBId9EYumMlM9iHCc2p_7s55C75XA7ka1A5iRO-1WzDeZZp_U2djLYdd80&_nc_ht=scontent.fewr1-6.fna&_nc_tp=6&oh=bbc6e3796eb9ebd4f76803d452e1654e&oe=5F00837D',
      'images/1.png',
      'https://scontent.fewr1-6.fna.fbcdn.net/v/t1.0-0/p640x640/86296455_2573592766189146_3731961409219592192_o.jpg?_nc_cat=102&_nc_oc=AQlun9NCstBId9EYumMlM9iHCc2p_7s55C75XA7ka1A5iRO-1WzDeZZp_U2djLYdd80&_nc_ht=scontent.fewr1-6.fna&_nc_tp=6&oh=bbc6e3796eb9ebd4f76803d452e1654e&oe=5F00837D',
      'images/1.png',
      'https://scontent.fewr1-6.fna.fbcdn.net/v/t1.0-0/p640x640/86296455_2573592766189146_3731961409219592192_o.jpg?_nc_cat=102&_nc_oc=AQlun9NCstBId9EYumMlM9iHCc2p_7s55C75XA7ka1A5iRO-1WzDeZZp_U2djLYdd80&_nc_ht=scontent.fewr1-6.fna&_nc_tp=6&oh=bbc6e3796eb9ebd4f76803d452e1654e&oe=5F00837D',
      'file:///C:/Users/Fernando/Pictures/Wedding/Wedding%20Tables/Table%20Themes/6.png'    ]
  };

  render() {
    const ROW_SIZE = 3;
/*
    let imgArr = new Array();
    for (var i = 0; i < this.state.photosList.length; i++)
      imgArr.push(<ImageLink imgName={this.state.photosList[i]}/>);
*/
    let trArr = [];
    let tdArr=[];
    for (var i = 0; i < this.state.photosList.length; i++) {
      tdArr.push(<ImageTD imgName={this.state.photosList[i]}/>);
      if ((i + 1) % ROW_SIZE === 0 || i === this.state.photosList.length-1) {
        trArr.push(<tr>{tdArr}></tr>);
        tdArr = [];
      }
    }

    return (
      <React.Fragment>
        <table>
          {trArr}
        </table>
      </React.Fragment>
    );
  }
}

export default Gallery;
