import React, { Component } from "react";

function ImageLink(props) {
    return (
      <div class="gallery">
        <a target="_blank" href={props.imgName}>
          <img src={props.imgName} width="600" height="400"/>
        </a>
      </div>
    );
}


class Gallery extends Component {
  state = {
    photosList: [
      'file:///C:/Users/Fernando/Pictures/Wedding/Wedding%20Tables/Table%20Themes/1.png',
      'file:///C:/Users/Fernando/Pictures/Wedding/Wedding%20Tables/Table%20Themes/2.png',
      'file:///C:/Users/Fernando/Pictures/Wedding/Wedding%20Tables/Table%20Themes/3.png',
      'file:///C:/Users/Fernando/Pictures/Wedding/Wedding%20Tables/Table%20Themes/4.png',
      'file:///C:/Users/Fernando/Pictures/Wedding/Wedding%20Tables/Table%20Themes/5.png',
      'file:///C:/Users/Fernando/Pictures/Wedding/Wedding%20Tables/Table%20Themes/6.png'    ]
  };

  render() {
    let imgArr = new Array();
    for (var i = 0; i < this.state.photosList.length; i++)
      imgArr.push(<ImageLink imgName={this.state.photosList[i]}/>);
    return (
      <React.Fragment>
        {imgArr}
      </React.Fragment>
    );
  }
}

export default Gallery;
